import React from 'react';
import { Link } from 'react-router-dom';
import './EventCard.css';

const EventCard = ({ event, onDelete }) => {

    const handleDelete = async () => {
        try {
            await fetch(`http://localhost:5000/api/events/${event._id}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                    // Include your auth token if required
                    // 'Authorization': `Bearer ${yourToken}`
                },
            });

            if (onDelete) {
                onDelete(event._id); // Notify parent component to refresh the event list
            }
        } catch (error) {
            console.error('Error deleting event:', error);
        }
    };

    return (
        <div className="event-card">
            <h3>{event.title}</h3>
            <p><strong>Date:</strong> {event.date}</p>
            <p><strong>Location:</strong> {event.location}</p>
            <p><strong>Description:</strong> {event.description}</p>
            <Link to={`/events/events${event.id}`}>Get Tickets</Link>
            <button onClick={handleDelete}>Delete</button>
        </div>
    );
}

export default EventCard;
